	bytesReceived = 0;
	bytesSent = 0;
    [[NSNotificationCenter defaultCenter] postNotificationName:SBC_LinkByteRateChanged object:self];
	[self performSelector:@selector(calculateRates) withObject:nil afterDelay:kSBCRateIntegrationTime];
}

