#pragma mark •••Bit Processing Protocol
- (void) processIsStarting
{
	//we will control the polling loop
	[NSObject cancelPreviousPerformRequestsWithTarget:self selector:@selector(pollHardware) object:nil];
    readOnce = NO;
	[self setInvolvedInProcess:YES];
}

- (void) processIsStopping
{
	//return control to the normal loop
	[self setPollTime:pollTime];
	[self setInvolvedInProcess:NO];
}

//note that everything called by these routines MUST be threadsafe
- (void) startProcessCycle
{    
    if(!readOnce){
        @try { 
            [self performSelectorOnMainThread:@selector(pollHardware) withObject:nil waitUntilDone:NO]; 
            readOnce = YES;
        }
		@catch(NSException* localException) { 
			//catch this here to prevent it from falling thru, but nothing to do.
        }
    }
}

- (void) endProcessCycle
{
	readOnce = NO;
}

- (NSString*) identifier
{
	NSString* s;
 	@synchronized(self){
		s= [NSString stringWithFormat:@"CP8,%d",[self uniqueIdNumber]];
	}
	return s;
}

- (NSString*) processingTitle
{
	NSString* s;
 	@synchronized(self){
		s= [self identifier];
	}
	return s;
}

- (BOOL) processValue:(int)channel
{
	BOOL theValue = 0;
	@synchronized(self){
		switch(channel){
			case 0: theValue = 	[self roughValveStatus];	break;
			case 1: theValue = 	[self purgeStatus];			break;
		}
	}
	return theValue;
}

- (void) setProcessOutput:(int)channel value:(int)value
{	
}
- (void) setOutputBit:(int)channel value:(int)value
{
	@synchronized(self){
		switch(channel){
			case 0: [self writeRoughValveOpen:value];	break;
			case 1: [self writePurgeValveOpen:value];	break;
		}
	}
}

