- (void) setupFormat
{
	if(formatSetting >= 0 && formatSetting <kNumberRad7FormatNames){
		[self addCmdToQueue:[NSString stringWithFormat: @"SETUP FORMAT %@",rad7FormatNames[formatSetting]]];
	}
